svg.script:

document.addEventListener('DOMContentLoaded', function() {
  // Interactive elements
  const workflowSteps = document.querySelectorAll('.workflow-step');
  const statusIndicators = document.querySelectorAll('.status-indicator');
  const aiButtons = document.querySelectorAll('.ai-accept-btn, .ai-modify-btn');
  const toggleSwitches = document.querySelectorAll('.toggle-switch');

  // Workflow step interaction
  workflowSteps.forEach(step => {
    step.addEventListener('click', function() {
      const stepType = this.getAttribute('data-step');
      showStepDetails(stepType);

      if (typeof window.showWorkflowStep === 'function') {
        window.showWorkflowStep(stepType);
      }
    });

    step.addEventListener('keydown', function(e) {
      if (e.key === 'Enter' || e.key === ' ') {
        e.preventDefault();
        this.click();
      }
    });
  });

  // AI assistance interaction
  aiButtons.forEach(btn => {
    btn.addEventListener('click', function() {
      const action = this.classList.contains('ai-accept-btn') ? 'accept' : 'modify';
      handleAIRecommendation(action);

      if (typeof window.handleAIAssistance === 'function') {
        window.handleAIAssistance(action);
      }
    });
  });

  // System component interaction
  const systemPanels = document.querySelectorAll('.ai-engine, .human-expertise, .knowledge-system');
  systemPanels.forEach(panel => {
    panel.addEventListener('click', function() {
      const panelType = this.classList[0];
      showSystemDetails(panelType);

      if (typeof window.showSystemPanel === 'function') {
        window.showSystemPanel(panelType);
      }
    });
  });

  // Configuration controls
  toggleSwitches.forEach(toggle => {
    toggle.addEventListener('click', function() {
      toggleSetting(this);

      if (typeof window.updateSystemSettings === 'function') {
        window.updateSystemSettings();
      }
    });
  });

  // Helper functions
  function showStepDetails(stepType) {
    if (typeof window.showStepDetails === 'function') {
      window.showStepDetails(stepType);
    } else {
      console.log(`Showing details for step: ${stepType}`);
    }
  }

  function handleAIRecommendation(action) {
    if (typeof window.handleAIRecommendation === 'function') {
      window.handleAIRecommendation(action);
    } else {
      console.log(`AI recommendation ${action}ed`);
    }
  }

  function showSystemDetails(panelType) {
    if (typeof window.showSystemDetails === 'function') {
      window.showSystemDetails(panelType);
    } else {
      console.log(`Showing ${panelType} details`);
    }
  }

  function toggleSetting(element) {
    const isOn = element.getAttribute('fill') === 'url(#successGradient)';
    element.setAttribute('fill', isOn ? '#E0E0E0' : 'url(#successGradient)');

    const nextText = element.nextElementSibling;
    if (nextText) {
      nextText.textContent = isOn ? 'OFF' : 'ON';
      nextText.setAttribute('fill', isOn ? '#666' : '#4CAF50');
    }
  }

  // Real-time updates
  function updateDashboard() {
    if (typeof window.updateReviewDashboard === 'function') {
      window.updateReviewDashboard();
    }
  }

  function updateMetrics() {
    if (typeof window.updatePerformanceMetrics === 'function') {
      window.updatePerformanceMetrics();
    }
  }

  // Initialize system
  if (typeof window.initializeReviewSystem === 'function') {
    window.initializeReviewSystem();
  }

  // Periodic updates
  setInterval(updateDashboard, 30000); // Update every 30 seconds
  setInterval(updateMetrics, 60000); // Update metrics every minute
});
