svg.script:

document.addEventListener('DOMContentLoaded', function() {
  // Interactive elements
  const strategyCards = document.querySelectorAll('.strategy-card');
  const riskCategories = document.querySelectorAll('.risk-category');
  const automationTools = document.querySelectorAll('.automation-tool');
  const actionButtons = document.querySelectorAll('.action-btn');
  const phaseProgress = document.querySelectorAll('.phase-progress');

  let selectedStrategy = 'phased'; // Default recommended strategy
  let modernizationState = {
    systemAnalysis: 100,
    riskAssessment: 85,
    migrationPlanning: 70,
    executionProgress: 45
  };

  // Strategy selection interaction
  strategyCards.forEach(card => {
    card.addEventListener('click', function() {
      const strategy = this.parentElement.getAttribute('data-strategy');
      selectStrategy(strategy);

      if (typeof window.selectMigrationStrategy === 'function') {
        window.selectMigrationStrategy(strategy);
      }
    });

    card.addEventListener('keydown', function(e) {
      if (e.key === 'Enter' || e.key === ' ') {
        e.preventDefault();
        this.click();
      }
    });
  });

  // Risk category interaction
  riskCategories.forEach(category => {
    category.addEventListener('click', function() {
      const riskType = this.getAttribute('data-risk');
      showRiskAnalysis(riskType);

      if (typeof window.showRiskAnalysis === 'function') {
        window.showRiskAnalysis(riskType);
      }
    });
  });

  // Automation tool interaction
  automationTools.forEach(tool => {
    tool.addEventListener('click', function() {
      const toolType = this.getAttribute('data-tool');
      runAutomationTool(toolType);

      if (typeof window.runAutomationTool === 'function') {
        window.runAutomationTool(toolType);
      }
    });
  });

  // Action button interaction
  actionButtons.forEach(btn => {
    btn.addEventListener('click', function() {
      const action = this.getAttribute('data-action');
      executeAction(action);

      if (typeof window.executeModernizationAction === 'function') {
        window.executeModernizationAction(action);
      }
    });
  });

  // Phase progress interaction
  phaseProgress.forEach(phase => {
    phase.addEventListener('click', function() {
      const phaseNum = this.getAttribute('data-phase');
      showPhaseDetails(phaseNum);

      if (typeof window.showPhaseDetails === 'function') {
        window.showPhaseDetails(phaseNum);
      }
    });
  });

  // Helper functions
  function selectStrategy(strategy) {
    selectedStrategy = strategy;

    // Update UI to highlight selected strategy
    strategyCards.forEach(card => {
      card.parentElement.classList.remove('selected');
    });

    const selectedCard = document.querySelector(`[data-strategy="${strategy}"] .strategy-card`);
    if (selectedCard) {
      selectedCard.parentElement.classList.add('selected');
    }

    updateRecommendations(strategy);
  }

  function updateRecommendations(strategy) {
    const recommendations = {
      'bigbang': 'High-risk, high-reward approach. Ensure comprehensive testing.',
      'phased': 'Recommended balanced approach with validation at each step.',
      'strangler': 'Lowest risk but longer timeline. Good for critical systems.',
      'hybrid': 'Flexible approach combining best of multiple strategies.'
    };

    if (typeof window.updateStrategyRecommendations === 'function') {
      window.updateStrategyRecommendations(recommendations[strategy]);
    }
  }

  function showRiskAnalysis(riskType) {
    const riskData = {
      'technical': {
        level: 'Medium',
        factors: ['Integration complexity', 'Data migration', 'Performance impact'],
        mitigation: 'Parallel systems, staged rollout, comprehensive testing'
      },
      'business': {
        level: 'Medium',
        factors: ['Service disruption', 'Timeline overruns', 'Cost escalation'],
        mitigation: 'Business continuity planning, phased approach, budget controls'
      }
    };

    if (typeof window.displayRiskAnalysis === 'function') {
      window.displayRiskAnalysis(riskData[riskType]);
    }
  }

  function runAutomationTool(toolType) {
    const tools = {
      'assessment': 'Running comprehensive code analysis...',
      'migration': 'Generating migration timeline and resource allocation...',
      'monitoring': 'Initializing real-time progress monitoring...',
      'optimization': 'Optimizing strategy based on current parameters...'
    };

    if (typeof window.runTool === 'function') {
      window.runTool(toolType, tools[toolType]);
    } else {
      console.log(tools[toolType]);
    }
  }

  function executeAction(action) {
    const actions = {
      'analyze': () => analyzeSystem(),
      'plan': () => generatePlan(),
      'assess': () => assessRisks(),
      'optimize': () => optimizeStrategy(),
      'execute': () => executePlan()
    };

    if (actions[action]) {
      actions[action]();
    }
  }

  function analyzeSystem() {
    modernizationState.systemAnalysis = 100;
    updateProgressDisplay();

    if (typeof window.performSystemAnalysis === 'function') {
      window.performSystemAnalysis();
    }
  }

  function generatePlan() {
    if (modernizationState.systemAnalysis >= 100) {
      modernizationState.migrationPlanning = 85;
      updateProgressDisplay();

      if (typeof window.generateMigrationPlan === 'function') {
        window.generateMigrationPlan(selectedStrategy);
      }
    }
  }

  function assessRisks() {
    modernizationState.riskAssessment = 90;
    updateProgressDisplay();

    if (typeof window.performRiskAssessment === 'function') {
      window.performRiskAssessment();
    }
  }

  function optimizeStrategy() {
    if (modernizationState.migrationPlanning >= 70) {
      selectedStrategy = 'phased'; // AI optimization result
      selectStrategy(selectedStrategy);

      if (typeof window.optimizeStrategy === 'function') {
        window.optimizeStrategy();
      }
    }
  }

  function executePlan() {
    if (modernizationState.migrationPlanning >= 80) {
      modernizationState.executionProgress = 15;
      updateProgressDisplay();

      if (typeof window.executeMigrationPlan === 'function') {
        window.executeMigrationPlan();
      }
    }
  }

  function updateProgressDisplay() {
    if (typeof window.updateModernizationProgress === 'function') {
      window.updateModernizationProgress(modernizationState);
    }
  }

  function showPhaseDetails(phaseNum) {
    const phaseDetails = {
      '1': 'Database modernization: Cloud migration, schema optimization',
      '2': 'API layer: RESTful services, microservices architecture',
      '3': 'UI components: Modern framework migration, responsive design',
      '4': 'Integration: System integration, testing, deployment'
    };

    if (typeof window.showPhaseDetails === 'function') {
      window.showPhaseDetails(phaseDetails[phaseNum]);
    }
  }

  // Real-time updates
  function updateModernizationMetrics() {
    if (typeof window.updateMetrics === 'function') {
      window.updateMetrics();
    }
  }

  function validateMigrationProgress() {
    if (typeof window.validateProgress === 'function') {
      window.validateProgress();
    }
  }

  // Initialize system
  if (typeof window.initializeModernizationPlanner === 'function') {
    window.initializeModernizationPlanner();
  }

  // Periodic updates
  setInterval(updateModernizationMetrics, 60000); // Update every minute
  setInterval(validateMigrationProgress, 120000); // Validate every 2 minutes
});
