svg.script:

document.addEventListener('DOMContentLoaded', function() {
  const onboardingPhases = document.querySelectorAll('.onboarding-phase');
  const actionBtns = document.querySelectorAll('.action-btn, .mentor-btn, .mobile-btn');
  const competencyItems = document.querySelectorAll('.competency-item');

  let onboardingState = {
    currentPhase: 2,
    completedPhases: [1],
    onboardeeId: 'sarah-chen',
    competencyScore: 7.8,
    engagementLevel: 94,
    mentorId: 'alex-rodriguez'
  };

  // Phase interactions
  onboardingPhases.forEach(phase => {
    phase.addEventListener('click', function() {
      const phaseNum = parseInt(this.getAttribute('data-phase'));
      showPhaseDetails(phaseNum);

      if (typeof window.exploreOnboardingPhase === 'function') {
        window.exploreOnboardingPhase(phaseNum);
      }
    });

    phase.addEventListener('keydown', function(e) {
      if (e.key === 'Enter' || e.key === ' ') {
        e.preventDefault();
        this.click();
      }
    });
  });

  // Action button handlers
  actionBtns.forEach(btn => {
    btn.addEventListener('click', function() {
      const action = this.getAttribute('data-action');
      handleOnboardingAction(action);
    });
  });

  // Competency item interactions
  competencyItems.forEach(item => {
    item.addEventListener('click', function() {
      showCompetencyDetails(item);
    });
  });

  function showPhaseDetails(phaseNum) {
    const phaseDetails = {
      1: { name: 'Foundation & Setup', duration: '2 days', status: 'completed', score: 8.5 },
      2: { name: 'Context Deep Dive', duration: '7 days', status: 'current', score: 7.8 },
      3: { name: 'Practical Application', duration: '5 days', status: 'pending', score: null },
      4: { name: 'Independent Mastery', duration: '7 days', status: 'pending', score: null }
    };

    if (typeof window.showPhaseDetails === 'function') {
      window.showPhaseDetails(phaseDetails[phaseNum]);
    } else {
      console.log(`Phase ${phaseNum}: ${phaseDetails[phaseNum].name} - ${phaseDetails[phaseNum].status}`);
    }
  }

  function handleOnboardingAction(action) {
    const actions = {
      'mentor-check': () => scheduleMentorCheckIn(),
      'progress-review': () => showProgressReview(),
      'message-mentor': () => messageMentor(),
      'schedule-meeting': () => scheduleMentorMeeting(),
      'quick-schedule': () => quickScheduleSession(),
      'provide-feedback': () => provideMentorFeedback(),
      'check-progress': () => checkOnboardingProgress(),
      'contact-mentor': () => contactPrimaryMentor(),
      'view-competencies': () => viewCompetencyScores(),
      'learning-paths': () => accessLearningPaths()
    };

    if (actions[action]) {
      actions[action]();
    }

    if (typeof window.handleOnboardingAction === 'function') {
      window.handleOnboardingAction(action);
    }
  }

  function scheduleMentorCheckIn() {
    if (typeof window.scheduleMentorCheckIn === 'function') {
      window.scheduleMentorCheckIn();
    } else {
      alert('Mentor check-in scheduled for today 3:00 PM with Alex Rodriguez');
    }
  }

  function showProgressReview() {
    if (typeof window.showProgressReview === 'function') {
      window.showProgressReview();
    } else {
      alert(`Progress Review: Phase 2/4 (50% complete), Competency: 7.8/10, Engagement: 94%`);
    }
  }

  function messageMentor() {
    if (typeof window.messageMentor === 'function') {
      window.messageMentor();
    } else {
      alert('Opening message interface with Alex Rodriguez...');
    }
  }

  function scheduleMentorMeeting() {
    if (typeof window.scheduleMentorMeeting === 'function') {
      window.scheduleMentorMeeting();
    } else {
      alert('Opening calendar to schedule meeting with Alex Rodriguez...');
    }
  }

  function quickScheduleSession() {
    if (typeof window.quickScheduleSession === 'function') {
      window.quickScheduleSession();
    } else {
      alert('Quick scheduling: Problem-solving session available tomorrow 2:00 PM');
    }
  }

  function provideMentorFeedback() {
    if (typeof window.provideMentorFeedback === 'function') {
      window.provideMentorFeedback();
    } else {
      alert('Opening mentor feedback form...');
    }
  }

  function checkOnboardingProgress() {
    if (typeof window.checkOnboardingProgress === 'function') {
      window.checkOnboardingProgress();
    } else {
      alert('Current progress: Phase 2/4, Day 4/7, Competency 7.8/10, 3 days remaining');
    }
  }

  function contactPrimaryMentor() {
    if (typeof window.contactPrimaryMentor === 'function') {
      window.contactPrimaryMentor();
    } else {
      alert('Contacting Alex Rodriguez (Primary Mentor) - Response time <30 min');
    }
  }

  function viewCompetencyScores() {
    if (typeof window.viewCompetencyScores === 'function') {
      window.viewCompetencyScores();
    } else {
      alert('Competency Scores: Technical 7.7/10, Collaboration 8.2/10, Overall 7.8/10');
    }
  }

  function accessLearningPaths() {
    if (typeof window.accessLearningPaths === 'function') {
      window.accessLearningPaths();
    } else {
      alert('Priority: Problem Solving, Enhancement: Context Creation, Optional: Architecture Patterns');
    }
  }

  function showCompetencyDetails(item) {
    if (typeof window.showCompetencyDetails === 'function') {
      window.showCompetencyDetails(item);
    }
  }

  // Real-time updates
  function updateOnboardingProgress() {
    if (typeof window.updateOnboardingProgress === 'function') {
      window.updateOnboardingProgress();
    }
  }

  function updateCompetencyScores() {
    if (typeof window.updateCompetencyScores === 'function') {
      window.updateCompetencyScores();
    }
  }

  // Initialize onboarding system
  if (typeof window.initializeOnboardingSystem === 'function') {
    window.initializeOnboardingSystem();
  }

  // Keyboard navigation for accessibility
  document.addEventListener('keydown', function(e) {
    if (e.key === 'ArrowRight') {
      // Navigate to next phase
      if (onboardingState.currentPhase < 4) {
        showPhaseDetails(onboardingState.currentPhase + 1);
      }
    } else if (e.key === 'ArrowLeft') {
      // Navigate to previous phase
      if (onboardingState.currentPhase > 1) {
        showPhaseDetails(onboardingState.currentPhase - 1);
      }
    }
  });

  // Periodic updates for progress tracking
  setInterval(updateOnboardingProgress, 60000); // Update every minute
  setInterval(updateCompetencyScores, 300000); // Update scores every 5 minutes
});
