svg.script:

document.addEventListener('DOMContentLoaded', function() {
  const feedbackSources = document.querySelectorAll('.feedback-source');
  const learningHub = document.querySelector('.learning-hub');
  const actionBtns = document.querySelectorAll('.action-btn, .mobile-btn');
  const learningInsights = document.querySelectorAll('.learning-insight');

  let feedbackData = {
    team: { satisfaction: 8.4, responses: 127 },
    delivery: { onTime: 94, quality: 96 },
    outcomes: { accuracy: 87, improvement: 5 },
    learning: { insights: 15, confidence: 92 }
  };

  // Feedback source interactions
  feedbackSources.forEach(source => {
    source.addEventListener('click', function() {
      const sourceType = this.getAttribute('data-source');
      showFeedbackDetails(sourceType);

      if (typeof window.exploreFeedbackSource === 'function') {
        window.exploreFeedbackSource(sourceType);
      }
    });

    source.addEventListener('keydown', function(e) {
      if (e.key === 'Enter' || e.key === ' ') {
        e.preventDefault();
        this.click();
      }
    });
  });

  // Learning hub interaction
  if (learningHub) {
    learningHub.addEventListener('click', function() {
      showLearningDetails();

      if (typeof window.exploreLearningEngine === 'function') {
        window.exploreLearningEngine();
      }
    });
  }

  // Action button handlers
  actionBtns.forEach(btn => {
    btn.addEventListener('click', function() {
      const action = this.getAttribute('data-action');
      handleAction(action);
    });
  });

  // Learning insight interactions
  learningInsights.forEach(insight => {
    insight.addEventListener('click', function() {
      const confidence = this.getAttribute('data-confidence');
      showInsightDetails(confidence, insight);
    });
  });

  function showFeedbackDetails(sourceType) {
    const details = {
      team: 'Team feedback shows 40% improvement in planning clarity',
      delivery: 'Delivery metrics indicate 15% improvement in cycle time',
      outcomes: 'Planning accuracy improved 5% this sprint',
      history: 'Historical analysis reveals seasonal velocity patterns',
      external: 'External factors tracking reduces dependency delays'
    };

    if (typeof window.showFeedbackDetails === 'function') {
      window.showFeedbackDetails(sourceType, details[sourceType]);
    } else {
      console.log(`Feedback Source: ${sourceType} - ${details[sourceType]}`);
    }
  }

  function showLearningDetails() {
    if (typeof window.showLearningDetails === 'function') {
      window.showLearningDetails(feedbackData.learning);
    } else {
      console.log('AI Learning Engine: 92% confidence, 15 insights generated');
    }
  }

  function handleAction(action) {
    const actions = {
      'collect-feedback': () => collectTeamFeedback(),
      'generate-insights': () => generateAIInsights(),
      'apply-high': () => applyHighConfidenceRecommendations(),
      'review-medium': () => reviewMediumConfidenceInsights(),
      'configure': () => configureLearningEngine(),
      'quick-feedback': () => showQuickFeedbackForm(),
      'view-insights': () => showLatestInsights(),
      'apply-recommendations': () => applyRecommendations(),
      'track-progress': () => showProgressTracking()
    };

    if (actions[action]) {
      actions[action]();
    }

    if (typeof window.handleFeedbackAction === 'function') {
      window.handleFeedbackAction(action);
    }
  }

  function collectTeamFeedback() {
    if (typeof window.collectTeamFeedback === 'function') {
      window.collectTeamFeedback();
    } else {
      alert('Team feedback collection initiated');
    }
  }

  function generateAIInsights() {
    if (typeof window.generateAIInsights === 'function') {
      window.generateAIInsights();
    } else {
      alert('Generating new AI insights from collected data');
    }
  }

  function applyHighConfidenceRecommendations() {
    if (typeof window.applyRecommendations === 'function') {
      window.applyRecommendations('high');
    } else {
      alert('Applying high confidence recommendations to planning process');
    }
  }

  function reviewMediumConfidenceInsights() {
    if (typeof window.reviewInsights === 'function') {
      window.reviewInsights('medium');
    } else {
      alert('Opening medium confidence insights for review');
    }
  }

  function configureLearningEngine() {
    if (typeof window.configureLearning === 'function') {
      window.configureLearning();
    } else {
      alert('Learning engine configuration panel would open here');
    }
  }

  function showQuickFeedbackForm() {
    if (typeof window.showQuickFeedback === 'function') {
      window.showQuickFeedback();
    } else {
      alert('Quick feedback form would appear here');
    }
  }

  function showLatestInsights() {
    if (typeof window.showLatestInsights === 'function') {
      window.showLatestInsights();
    } else {
      alert('Latest AI insights: Planning accuracy +5%, Team satisfaction +40%');
    }
  }

  function applyRecommendations() {
    if (typeof window.applyRecommendations === 'function') {
      window.applyRecommendations('all');
    } else {
      alert('Applying all available AI recommendations');
    }
  }

  function showProgressTracking() {
    if (typeof window.showProgressTracking === 'function') {
      window.showProgressTracking();
    } else {
      alert('Progress tracking: 60% complete, next milestone in 3 weeks');
    }
  }

  function showInsightDetails(confidence, element) {
    if (typeof window.showInsightDetails === 'function') {
      window.showInsightDetails(confidence, element);
    }
  }

  // Real-time updates
  function updateFeedbackMetrics() {
    if (typeof window.updateFeedbackMetrics === 'function') {
      window.updateFeedbackMetrics();
    }
  }

  function updateLearningProgress() {
    if (typeof window.updateLearningProgress === 'function') {
      window.updateLearningProgress();
    }
  }

  // Initialize system
  if (typeof window.initializeFeedbackLoop === 'function') {
    window.initializeFeedbackLoop();
  }

  // Keyboard navigation for accessibility
  document.addEventListener('keydown', function(e) {
    if (e.key === 'Tab') {
      // Enhanced tab navigation for complex interface
      const focusableElements = document.querySelectorAll('.interactive-element, .action-btn, .mobile-btn');
      // Tab navigation logic would be implemented here
    }
  });

  // Periodic updates for real-time feedback
  setInterval(updateFeedbackMetrics, 30000); // Update every 30 seconds
  setInterval(updateLearningProgress, 60000); // Update learning every minute
});
