svg.script:

document.addEventListener('DOMContentLoaded', function() {
  // Interactive elements
  const depthLayers = document.querySelectorAll('.depth-layer');
  const escalationPaths = document.querySelectorAll('.escalation-path');
  const actionButtons = document.querySelectorAll('.next-action-btn, .escalate-btn');
  const progressSteps = document.querySelectorAll('.progress-step');

  let currentLayer = 4; // Currently on Layer 4
  let reviewState = {
    timeUsed: 27,
    timebudget: 45,
    qualityScore: 85,
    qualityTarget: 94,
    aiConfidence: {
      syntax: 97,
      logic: 73,
      security: 45
    }
  };

  // Depth layer interaction
  depthLayers.forEach(layer => {
    layer.addEventListener('click', function() {
      const layerNum = parseInt(this.getAttribute('data-layer'));
      exploreLayer(layerNum);

      if (typeof window.exploreReviewDepth === 'function') {
        window.exploreReviewDepth(layerNum);
      }
    });

    layer.addEventListener('keydown', function(e) {
      if (e.key === 'Enter' || e.key === ' ') {
        e.preventDefault();
        this.click();
      }
    });
  });

  // Escalation path interaction
  escalationPaths.forEach(path => {
    path.addEventListener('click', function() {
      const fromLayer = this.getAttribute('data-from');
      const toLayer = this.getAttribute('data-to');
      showEscalationDetails(fromLayer, toLayer);

      if (typeof window.showEscalationPath === 'function') {
        window.showEscalationPath(fromLayer, toLayer);
      }
    });
  });

  // Action button interaction
  actionButtons.forEach(btn => {
    btn.addEventListener('click', function() {
      const action = this.classList.contains('next-action-btn') ? 'continue' : 'escalate';
      handleReviewAction(action);

      if (typeof window.handleReviewAction === 'function') {
        window.handleReviewAction(action);
      }
    });
  });

  // Progress step interaction
  progressSteps.forEach(step => {
    step.addEventListener('click', function() {
      const stepElement = this;
      showStepDetails(stepElement);

      if (typeof window.showProgressStep === 'function') {
        window.showProgressStep(stepElement);
      }
    });
  });

  // Helper functions
  function exploreLayer(layerNum) {
    // Update layer highlighting
    depthLayers.forEach(layer => {
      layer.classList.remove('selected');
    });

    const selectedLayer = document.querySelector(`[data-layer="${layerNum}"]`);
    if (selectedLayer) {
      selectedLayer.classList.add('selected');
    }

    updateLayerInfo(layerNum);
  }

  function updateLayerInfo(layerNum) {
    const layerInfo = {
      1: { name: 'Surface Scanning', cost: 5, time: '2-5 min', roi: 'Quick wins' },
      2: { name: 'Structural Validation', cost: 25, time: '15-30 min', roi: 'Structure issues' },
      3: { name: 'Logical Structure', cost: 60, time: '30-60 min', roi: 'Logic errors' },
      4: { name: 'Performance Analysis', cost: 120, time: '1-2 hours', roi: 'Performance issues' },
      5: { name: 'Architectural Analysis', cost: 240, time: '2-4 hours', roi: 'Critical issues only' }
    };

    if (typeof window.updateLayerDetails === 'function') {
      window.updateLayerDetails(layerInfo[layerNum]);
    }
  }

  function showEscalationDetails(fromLayer, toLayer) {
    if (typeof window.showEscalationDetails === 'function') {
      window.showEscalationDetails(fromLayer, toLayer);
    } else {
      console.log(`Escalation from Layer ${fromLayer} to Layer ${toLayer}`);
    }
  }

  function handleReviewAction(action) {
    if (action === 'continue') {
      // Continue current layer analysis
      updateReviewProgress();
    } else if (action === 'escalate') {
      // Escalate to next layer
      escalateToNextLayer();
    }
  }

  function updateReviewProgress() {
    reviewState.timeUsed += 5;
    reviewState.qualityScore += 2;

    if (typeof window.updateReviewState === 'function') {
      window.updateReviewState(reviewState);
    }
  }

  function escalateToNextLayer() {
    if (currentLayer < 5) {
      currentLayer++;
      updateProgressDisplay();

      if (typeof window.escalateReview === 'function') {
        window.escalateReview(currentLayer);
      }
    }
  }

  function updateProgressDisplay() {
    // Update progress step visualization
    progressSteps.forEach((step, index) => {
      const stepNum = index + 1;
      step.classList.remove('completed', 'active', 'pending');

      if (stepNum < currentLayer) {
        step.classList.add('completed');
      } else if (stepNum === currentLayer) {
        step.classList.add('active');
      } else {
        step.classList.add('pending');
      }
    });
  }

  function showStepDetails(stepElement) {
    if (typeof window.showStepDetails === 'function') {
      window.showStepDetails(stepElement);
    }
  }

  // Real-time updates
  function updateDepthAnalysis() {
    if (typeof window.updateDepthAnalysis === 'function') {
      window.updateDepthAnalysis();
    }
  }

  function updateAIConfidence() {
    if (typeof window.updateAIConfidence === 'function') {
      window.updateAIConfidence();
    }
  }

  // Initialize system
  if (typeof window.initializeDepthControl === 'function') {
    window.initializeDepthControl();
  }

  // Periodic updates
  setInterval(updateDepthAnalysis, 30000); // Update every 30 seconds
  setInterval(updateAIConfidence, 60000); // Update AI confidence every minute
});
