svg.script:

document.addEventListener('DOMContentLoaded', function() {
  const matrixCells = document.querySelectorAll('.matrix-cell');
  const dimensionBtns = document.querySelectorAll('.dimension-btn');
  const viewBtns = document.querySelectorAll('.view-btn');
  let currentView = '3d';

  // Matrix cell interactions
  matrixCells.forEach(cell => {
    cell.addEventListener('click', function() {
      // Remove previous selections
      matrixCells.forEach(c => c.classList.remove('selected'));

      // Select current cell
      this.classList.add('selected');

      // Update recommendation panel
      updateRecommendation(this);
    });

    cell.addEventListener('keydown', function(e) {
      if (e.key === 'Enter' || e.key === ' ') {
        e.preventDefault();
        this.click();
      }
    });
  });

  // Dimension navigation
  dimensionBtns.forEach(btn => {
    btn.addEventListener('click', function() {
      const dimension = this.getAttribute('data-dimension');
      highlightDimension(dimension);
    });
  });

  // View switching
  viewBtns.forEach(btn => {
    btn.addEventListener('click', function() {
      const view = this.getAttribute('data-view');
      switchView(view);
    });
  });

  function updateRecommendation(cell) {
    const testType = cell.getAttribute('data-test');
    const complexity = cell.getAttribute('data-complexity');
    const aiLevel = cell.getAttribute('data-ai');

    // Update current selection display
    const selectionText = `${capitalizeFirst(testType)} Testing √ó ${capitalizeFirst(complexity)} Complexity √ó ${aiLevel === 'manual' ? 'Manual' : aiLevel === 'assisted' ? 'AI Assisted' : 'Automated'}`;
    document.getElementById('current-selection').textContent = selectionText;

    // Generate recommendation preview
    const recommendation = generateRecommendation(testType, complexity, aiLevel);
    document.getElementById('recommendation-preview').textContent = recommendation;
  }

  function generateRecommendation(testType, complexity, aiLevel) {
    const recommendations = {
      'unit-simple-manual': 'Basic TDD approach with standard frameworks',
      'unit-simple-assisted': 'AI-generated test skeletons with manual refinement',
      'unit-simple-automated': 'Fully automated test generation and maintenance',
      'unit-moderate-assisted': 'Hybrid approach with 60% automation',
      'integration-moderate-assisted': 'Recommended: Hybrid approach with 60% automation',
      'integration-complex-assisted': 'AI-assisted enterprise testing strategy',
      'e2e-simple-assisted': 'AI-generated user journey tests with validation'
    };

    const key = `${testType}-${complexity}-${aiLevel}`;
    return recommendations[key] || 'Custom strategy recommended based on selection';
  }

  function highlightDimension(dimension) {
    // Visual highlighting logic for dimension exploration
    const layers = document.querySelectorAll('.test-type-layer');
    layers.forEach(layer => {
      layer.style.opacity = dimension === 'test-types' ? '1' : '0.3';
    });

    setTimeout(() => {
      layers.forEach(layer => layer.style.opacity = '1');
    }, 2000);
  }

  function switchView(view) {
    currentView = view;
    const matrix3D = document.getElementById('matrix-3d');

    viewBtns.forEach(btn => {
      btn.classList.remove('active');
      if (btn.getAttribute('data-view') === view) {
        btn.classList.add('active');
      }
    });

    if (view === '2d') {
      matrix3D.style.transform = 'none';
      matrix3D.style.perspective = 'none';

      // Flatten all layers
      const layers = document.querySelectorAll('.test-type-layer');
      layers.forEach((layer, index) => {
        layer.style.transform = `translate(${index * 150}px, 0)`;
      });
    } else {
      matrix3D.style.transform = '';
      matrix3D.style.perspective = '1000px';

      // Restore 3D positioning
      const layers = document.querySelectorAll('.test-type-layer');
      layers.forEach((layer, index) => {
        layer.style.transform = `translate(0, ${-index * 100}px)`;
      });
    }
  }

  function capitalizeFirst(str) {
    return str.charAt(0).toUpperCase() + str.slice(1);
  }

  // Initialize with default selection
  const defaultCell = document.querySelector('.matrix-cell.recommended');
  if (defaultCell) {
    updateRecommendation(defaultCell);
  }

  // Keyboard navigation for accessibility
  let focusedCellIndex = 0;
  const cells = Array.from(matrixCells);

  document.addEventListener('keydown', function(e) {
    if (e.key === 'ArrowRight' || e.key === 'ArrowLeft' || 
        e.key === 'ArrowUp' || e.key === 'ArrowDown') {
      e.preventDefault();

      cells[focusedCellIndex].classList.remove('focused');

      if (e.key === 'ArrowRight') focusedCellIndex = Math.min(focusedCellIndex + 1, cells.length - 1);
      if (e.key === 'ArrowLeft') focusedCellIndex = Math.max(focusedCellIndex - 1, 0);
      if (e.key === 'ArrowUp') focusedCellIndex = Math.max(focusedCellIndex - 3, 0);
      if (e.key === 'ArrowDown') focusedCellIndex = Math.min(focusedCellIndex + 3, cells.length - 1);

      cells[focusedCellIndex].classList.add('focused');
      cells[focusedCellIndex].focus();
    }
  });
});
